<?php global $pmc_data; ?>		<?php 		$day = get_the_time('d');		$month= get_the_time('m');		$year= get_the_time('Y');		?>			<div class = "meta">			<h2 class="title"><a href="<?php the_permalink() ?>" rel="bookmark" title="Permanent Link to <?php the_title_attribute(); ?>"><?php the_title(); ?></a></h2>						<div class = "post-author">					<a class="post-meta-author" href="<?php echo  the_author_meta( 'user_url' ) ?>"><?php _e('by ','pmc-themes');?> <span><?php echo get_the_author(); ?></span></a>			</div>			<div class = "post-time">				<a class="post-meta-time" href="<?php echo get_day_link( $year, $month, $day ) ?>"><?php _e('ON ','pmc-themes'). the_time('F j, Y') ?></a>			</div>						<div class="blogContent">				<div class="blogcontent"><?php the_excerpt() ?></div>			</div>		</div>		<div class="bottom-blog">			<div class="blog-date"><a class="post-meta-comments" href="<?php the_permalink() ?>#commentform"><?php comments_number(); ?></a></div>			<div class="pmc-read-more">				<a  href="<?php echo get_permalink( get_the_id() ) ?>"><?php _e('Continue reading','pmc-themes') ?></a>			</div>				<div class="social-blog"><?php pmc_socialLinkSingle(get_permalink(),get_the_title()) ?>	</a></div>		</div>		